import heroImage from "./assets/img/hero-image.jpg";

const Hero = () => {
  return (
    
   <div className="hero flex p-2">


    <div>

    <h1 className="text-xl md:text-3xl lg:text-4xl font-semibold mb-4 Inter">Welcome and Enjoy heartwarming <span style={{ color: '#39DB4A' }}>Food</span></h1>
    <p className="hero-text">

 Enjoy mouth watering dishes from all over the world.
 Lifetime experience with discovery with every culinary plate.
</p>
</div>

    

    
    


<div className='bg-green-500'></div>
</div>
   
  );
};
  	
export default Hero;
