import React from 'react';
import icon1 from "./assets/img/Mask group (1).png";
import icon2 from "./assets/img/Mask group (2).png";
 import icon3 from "./assets/img/Mask group (3).png";

const Testimonials = () => {
  return (
    
<div class=" flex py-20 px-10 md:px-20 lg:px-32 ">

<div class="md:w-1/2">

<img src={heroImage} alt="hero" h-auto w-100 />

</div>
  <div class="  text-center justify-center max-w-screen-xl mx-auto pl-20 ">


    <h1 className="text-xl text-orange-500  font-bold mb-4 Inter md:text-3xl lg:text-4xl">Testimonials</h1>
    <div class=" flex flex-col text-center justify-center  mb-10 md:mb-0 text-center ">
    
      <p class=" text-center justify-center font-bold text-xl  md:text-2xl "> What our Customers say about us</p>

      <p class="text-base font-bold  md:text-lg ">

      “I had the pleasure of dining at Foodi last night,
       and I'm still raving about the experience! The attention to detail in presentation and service was impeccable”
      </p>
    

    <div class="relative">
    <img src={icon1} alt="Image 1" class="rounded-full w-32 h-32 absolute top-0 left-0" />
    <img src={icon2} alt="Image 2" class="rounded-full w-32 h-32 absolute top-0 left-0" />
    <img src={icon3} alt="Image 2" class="rounded-full w-32 h-32 absolute top-0 left-0" />
    </div>
    

  
    </div>
   
    </div>
    </div>
   
   
 )
  }
export default Testimonials;
