import React from 'react';
import heroImage from "./assets/img/hero-image.jpg";

const Hero = () => {
  return (
    
<div class="bg-gray-900 text-white py-20 px-10 md:px-20 lg:px-32 flex items-center justify-center">
  <div class="flex flex-col md:flex-row items-center max-w-screen-xl mx-auto">
   


    <div className="flex flex-col items-center max-w-screen-xl mx-auto">

    <h1 className="text-xl md:text-3xl lg:text-4xl font-semibold mb-4 Inter">Welcome and Enjoy heartwarming <span style={{ color: '#39DB4A' }}>Food</span></h1>
    <div class="md:w-1/2 md:pr-10 mb-10 md:mb-0">
      <h1 class="text-4xl md:text-5xl lg:text-6xl font-bold leading-tight mb-4">Two Line Header</h1>
      <p class="text-lg lg:text-xl"> Enjoy mouth watering dishes from all over the world.
 Lifetime experience with discovery with every culinary plate..</p>
    </div>


    

    
    
<div class="md:w-1/2">

<img src={heroImage} alt="hero" h-100 w-100 />

</div>
   
</div>
  </div>
</div>
  )
  }
export default Hero;
